OrDislike+"="+newsId,true)			req.send()					}//end like_dislike				function likeClick(ID){		var newsId=ID.split("_")[ID.split("_").length-1]		if (document.getElementById(ID).innerHTML.search("off")!=-1){			if (document.getElementById(ID).innerHTML.search("dis")!=-1){				like_dislike(newsId,"dislike")								document.getElementById(ID).innerHTML=				"<img src=\"./img/dislike_circle_button_on.png\" style=\"max-width:65%\" alt=\"like\"/>"				document.getElementById(ID.substr(3)).innerHTML=				"<img src=\"./img/like_circle_button_off.png\" style=\"max-width:65%\" alt=\"like\"/>"											}else{				like_dislike(newsId,"like")				document.getElementById(ID).innerHTML=				"<img src=\"./img/like_circle_button_on.png\" style=\"max-width:65%\" alt=\"like\"/>"								document.getElementById("dis"+ID).innerHTML=				"<img src=\"./img/dislike_circle_button_off.png\" style=\"max-width:65%\" alt=\"like\"/>"							}//end else dis 		}else{						if (document.getElementById(ID).innerHTML.search("dis")!=-1){				like_dislike(newsId,"dislike")				document.getElementById(ID).innerHTML=				"<img src=\"./img/dislike_circle_button_off.png\" style=\"max-width:65%\" alt=\"like\"/>"				}			else{				like_dislike(newsId,"like")				document.getElementById(ID).innerHTML=				"<img src=\"./img/like_circle_button_off.png\" style=\"max-width:65%\" alt=\"like\"/>"				}				}					}//end likeClick				function getContextFromServer(newsId){					var req=new XMLHttpRequest()			req.open("GET","edit_news.php?getContext="+newsId,false)			req.send();			return req.responseText				}//end get context from server				function getMoreLimit(){					var req=new XMLHttpRequest()			req.open("GET","edit_news.php?getMoreLimit=1",false)			req.send();				return req.responseText				}//end get context from server				function editClick(ID){			editClick.editFlag=(++editClick.editFlag)%2||0			splitedId=ID.split("_")			var newsId=splitedId[splitedId.length-1]						if (!editClick.editFlag){			//var context=document.getElementById('context_'+newsId).innerHTML			var context=getContextFromServer(newsId)			context=context.trim()			if (context.length>1000){				var rows=12				var cols=50			}else{				var rows=7				var cols=50			}			var title=document.getElementById('title_'+newsId).innerHTML			title=title.trim()			document.getElementById(ID).innerHTML=			"<img src=\"./img/save_circle_button.png\" style=\"max-width:65%\" alt=\"save\"/>"						document.getElementById('context_'+newsId).			innerHTML="<textarea rows=\""+rows+"\" cols=\""+cols			+"\"id=\"editContext_"+newsId+"\" >"+context+"</textarea>"						document.getElementById('title_'+newsId).innerHTML=			"<input style=\"text-align:center;\" id=\"editTitle_"			+newsId+"\" type=\"text\" value=\""+			title+"\"> </input>"			}//end if edit flag			else{						document.getElementById(ID).innerHTML=			"<img src=\"./img/edit_circle_button.png\" style=\"max-width:65%\" alt=\"edit\"/>"			var title=document.getElementById("editTitle_"+newsId).value;			var context=document.getElementById("editContext_"+newsId).value;			var req=new XMLHttpRequest()			var reqParams=			"title="+encodeURIComponent(title)+			"&context="+encodeURIComponent(context)+			"&newsId="+encodeURIComponent(newsId)			//alert(reqParams)			req.open("POST","edit_news.php",false)			req.setRequestHeader("Content-type","application/x-www-form-urlencoded")			req.setRequestHeader("Content-Length",reqParams.length)			//req.setReqestHeader("Connection","close")			req.send(reqParams)						document.getElementById('title_'+newsId).innerHTML=title			moreLimit=getMoreLimit()			miniContext=context.substr(0,moreLimit)						if (context.length>moreLimit &&				!document.getElementById("more_"+newsId) ){								/*var htmlContent="<button type=\"button\" id=\"more_"+newsId+ 				" onClick=\"moreClick("+newsId+")\" style=\"border: 0;"+ 				"background: transparent\">"+				"<img src=\"./img/more_button.png\" alt=\"more\"/></button>";				*/								buttonTr=document.getElementById("buttonContainer")								var cell=buttonTr.insertCell()				var btn=document.createElement("BUTTON")				var img=document.createElement("img")												img.src="./img/more_circle_button.png"				img.style.maxWidth="65%"				btn.appendChild(img)				btn.id="more_"+newsId				btn.style.border="0"								btn.style.background="transparent"				btn.addEventListener("click",function() {				moreClick(newsId) 				})								cell.appendChild(btn)										}else if (context.length<moreLimit) {							var element=document.getElementById("more_"+newsId);							if (element)						element.parentNode.removeChild(element)								}						document.getElementById('context_'+newsId).innerHTML=			miniContext.replace(/(?:\r\n|\r|\n)/g, '<br />')			}					}        function autoResizeDiv()        {            document.getElementById('main').style.height = window.innerHeight*0.9 +'px';            document.getElementById('main').style.width = window.innerWidth *0.9+'px';        }        window.onresize = autoResizeDiv;        autoResizeDiv();</script></body></html><?php }} ?>
